using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class AssociadosBusiness    {        public List<Associados> List()        {            return new AssociadosRepository().List();        }        public Associados Get(int idassociados)        {            return new AssociadosRepository().Get(idassociados);        }        public Int32 Update(Associados associados)        {            return new AssociadosRepository().Put(associados);        }        public Int32 Insert(Associados associados)        {            return new AssociadosRepository().Post(associados);        }        public Int32 Delete(Int32 idassociados)        {            return new AssociadosRepository().Delete(idassociados);        }    }}