using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class AtivacoesBusiness    {        public List<Ativacoes> List()        {            return new AtivacoesRepository().List();        }        public Ativacoes Get(int idativacoes)        {            return new AtivacoesRepository().Get(idativacoes);        }        public Int32 Update(Ativacoes ativacoes)        {            return new AtivacoesRepository().Put(ativacoes);        }        public Int32 Insert(Ativacoes ativacoes)        {            return new AtivacoesRepository().Post(ativacoes);        }        public Int32 Delete(Int32 idativacoes)        {            return new AtivacoesRepository().Delete(idativacoes);        }    }}