using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class BancosBusiness    {        public List<Bancos> List()        {            return new BancosRepository().List();        }        public Bancos Get(int idbancos)        {            return new BancosRepository().Get(idbancos);        }        public Int32 Update(Bancos bancos)        {            return new BancosRepository().Put(bancos);        }        public Int32 Insert(Bancos bancos)        {            return new BancosRepository().Post(bancos);        }        public Int32 Delete(Int32 idbancos)        {            return new BancosRepository().Delete(idbancos);        }    }}