using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class ConfiguracoesBusiness    {        public List<Configuracoes> List()        {            return new ConfiguracoesRepository().List();        }        public Configuracoes Get(int idconfiguracoes)        {            return new ConfiguracoesRepository().Get(idconfiguracoes);        }        public Int32 Update(Configuracoes configuracoes)        {            return new ConfiguracoesRepository().Put(configuracoes);        }        public Int32 Insert(Configuracoes configuracoes)        {            return new ConfiguracoesRepository().Post(configuracoes);        }        public Int32 Delete(Int32 idconfiguracoes)        {            return new ConfiguracoesRepository().Delete(idconfiguracoes);        }    }}