using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class ContacorrenteBusiness    {        public List<Contacorrente> List()        {            return new ContacorrenteRepository().List();        }        public Contacorrente Get(int idcontacorrente)        {            return new ContacorrenteRepository().Get(idcontacorrente);        }        public Int32 Update(Contacorrente contacorrente)        {            return new ContacorrenteRepository().Put(contacorrente);        }        public Int32 Insert(Contacorrente contacorrente)        {            return new ContacorrenteRepository().Post(contacorrente);        }        public Int32 Delete(Int32 idcontacorrente)        {            return new ContacorrenteRepository().Delete(idcontacorrente);        }    }}