using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class DadosbancariosassociadosBusiness    {        public List<Dadosbancariosassociados> List()        {            return new DadosbancariosassociadosRepository().List();        }        public Dadosbancariosassociados Get(int iddadosbancariosassociados)        {            return new DadosbancariosassociadosRepository().Get(iddadosbancariosassociados);        }        public Int32 Update(Dadosbancariosassociados dadosbancariosassociados)        {            return new DadosbancariosassociadosRepository().Put(dadosbancariosassociados);        }        public Int32 Insert(Dadosbancariosassociados dadosbancariosassociados)        {            return new DadosbancariosassociadosRepository().Post(dadosbancariosassociados);        }        public Int32 Delete(Int32 iddadosbancariosassociados)        {            return new DadosbancariosassociadosRepository().Delete(iddadosbancariosassociados);        }    }}