using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class DetalhepedidoBusiness    {        public List<Detalhepedido> List()        {            return new DetalhepedidoRepository().List();        }        public Detalhepedido Get(int iddetalhepedido)        {            return new DetalhepedidoRepository().Get(iddetalhepedido);        }        public Int32 Update(Detalhepedido detalhepedido)        {            return new DetalhepedidoRepository().Put(detalhepedido);        }        public Int32 Insert(Detalhepedido detalhepedido)        {            return new DetalhepedidoRepository().Post(detalhepedido);        }        public Int32 Delete(Int32 iddetalhepedido)        {            return new DetalhepedidoRepository().Delete(iddetalhepedido);        }    }}