using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class EmpresaBusiness    {        public List<Empresa> List()        {            return new EmpresaRepository().List();        }        public Empresa Get(int idempresa)        {            return new EmpresaRepository().Get(idempresa);        }        public Int32 Update(Empresa empresa)        {            return new EmpresaRepository().Put(empresa);        }        public Int32 Insert(Empresa empresa)        {            return new EmpresaRepository().Post(empresa);        }        public Int32 Delete(Int32 idempresa)        {            return new EmpresaRepository().Delete(idempresa);        }    }}