using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class LancamentoscontacorrenteBusiness    {        public List<Lancamentoscontacorrente> List()        {            return new LancamentoscontacorrenteRepository().List();        }        public Lancamentoscontacorrente Get(int idlancamentoscontacorrente)        {            return new LancamentoscontacorrenteRepository().Get(idlancamentoscontacorrente);        }        public Int32 Update(Lancamentoscontacorrente lancamentoscontacorrente)        {            return new LancamentoscontacorrenteRepository().Put(lancamentoscontacorrente);        }        public Int32 Insert(Lancamentoscontacorrente lancamentoscontacorrente)        {            return new LancamentoscontacorrenteRepository().Post(lancamentoscontacorrente);        }        public Int32 Delete(Int32 idlancamentoscontacorrente)        {            return new LancamentoscontacorrenteRepository().Delete(idlancamentoscontacorrente);        }    }}