using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class PagamentopedidoBusiness    {        public List<Pagamentopedido> List()        {            return new PagamentopedidoRepository().List();        }        public Pagamentopedido Get(int idpagamentopedido)        {            return new PagamentopedidoRepository().Get(idpagamentopedido);        }        public Int32 Update(Pagamentopedido pagamentopedido)        {            return new PagamentopedidoRepository().Put(pagamentopedido);        }        public Int32 Insert(Pagamentopedido pagamentopedido)        {            return new PagamentopedidoRepository().Post(pagamentopedido);        }        public Int32 Delete(Int32 idpagamentopedido)        {            return new PagamentopedidoRepository().Delete(idpagamentopedido);        }    }}