using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class PedidoscomissionadosBusiness    {        public List<Pedidoscomissionados> List()        {            return new PedidoscomissionadosRepository().List();        }        public Pedidoscomissionados Get(int idpedidoscomissionados)        {            return new PedidoscomissionadosRepository().Get(idpedidoscomissionados);        }        public Int32 Update(Pedidoscomissionados pedidoscomissionados)        {            return new PedidoscomissionadosRepository().Put(pedidoscomissionados);        }        public Int32 Insert(Pedidoscomissionados pedidoscomissionados)        {            return new PedidoscomissionadosRepository().Post(pedidoscomissionados);        }        public Int32 Delete(Int32 idpedidoscomissionados)        {            return new PedidoscomissionadosRepository().Delete(idpedidoscomissionados);        }    }}