using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class PontoapoioBusiness    {        public List<Pontoapoio> List()        {            return new PontoapoioRepository().List();        }        public Pontoapoio Get(int idpontoapoio)        {            return new PontoapoioRepository().Get(idpontoapoio);        }        public Int32 Update(Pontoapoio pontoapoio)        {            return new PontoapoioRepository().Put(pontoapoio);        }        public Int32 Insert(Pontoapoio pontoapoio)        {            return new PontoapoioRepository().Post(pontoapoio);        }        public Int32 Delete(Int32 idpontoapoio)        {            return new PontoapoioRepository().Delete(idpontoapoio);        }    }}