using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class ProdutosBusiness    {        public List<Produtos> List()        {            return new ProdutosRepository().List();        }        public Produtos Get(int idprodutos)        {            return new ProdutosRepository().Get(idprodutos);        }        public Int32 Update(Produtos produtos)        {            return new ProdutosRepository().Put(produtos);        }        public Int32 Insert(Produtos produtos)        {            return new ProdutosRepository().Post(produtos);        }        public Int32 Delete(Int32 idprodutos)        {            return new ProdutosRepository().Delete(idprodutos);        }    }}