using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class TipoadesaoBusiness    {        public List<Tipoadesao> List()        {            return new TipoadesaoRepository().List();        }        public Tipoadesao Get(int idtipoassociado)        {            return new TipoadesaoRepository().Get(idtipoassociado);        }        public Int32 Update(Tipoadesao tipoadesao)        {            return new TipoadesaoRepository().Put(tipoadesao);        }        public Int32 Insert(Tipoadesao tipoadesao)        {            return new TipoadesaoRepository().Post(tipoadesao);        }        public Int32 Delete(Int32 idtipoassociado)        {            return new TipoadesaoRepository().Delete(idtipoassociado);        }    }}