using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class TipomovimentacaoBusiness    {        public List<Tipomovimentacao> List()        {            return new TipomovimentacaoRepository().List();        }        public Tipomovimentacao Get(int idtipomovimentacao)        {            return new TipomovimentacaoRepository().Get(idtipomovimentacao);        }        public Int32 Update(Tipomovimentacao tipomovimentacao)        {            return new TipomovimentacaoRepository().Put(tipomovimentacao);        }        public Int32 Insert(Tipomovimentacao tipomovimentacao)        {            return new TipomovimentacaoRepository().Post(tipomovimentacao);        }        public Int32 Delete(Int32 idtipomovimentacao)        {            return new TipomovimentacaoRepository().Delete(idtipomovimentacao);        }    }}