using drhipocratesCore.Model;using drhipocratesCore.Repository;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Business{    public class TipousuarioBusiness    {        public List<Tipousuario> List()        {            return new TipousuarioRepository().List();        }        public Tipousuario Get(int idtipousuario)        {            return new TipousuarioRepository().Get(idtipousuario);        }        public Int32 Update(Tipousuario tipousuario)        {            return new TipousuarioRepository().Put(tipousuario);        }        public Int32 Insert(Tipousuario tipousuario)        {            return new TipousuarioRepository().Post(tipousuario);        }        public Int32 Delete(Int32 idtipousuario)        {            return new TipousuarioRepository().Delete(idtipousuario);        }    }}