using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class AssociadosController : ControllerBase    {        [HttpGet]        public IEnumerable<Associados> List()        {            return new AssociadosService().List();        }        [HttpGet("{idassociados}", Name = "GetAssociados")]        public Associados Get(int idassociados)        {            return new AssociadosService().Get(idassociados);        }


        [HttpGet("{idassociados}", Name = "GetAllInformation")]        public Associados GetAllInformation(int idassociados)        {            return new AssociadosService().Get(idassociados);        }        [HttpPut]        public Int32 Put(Associados associados)        {            return new AssociadosService().Insert(associados);        }        [HttpPatch]        public Int32 Post(Associados associados)        {            return new AssociadosService().Update(associados);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idassociados)        {            return new AssociadosService().Delete(idassociados);        }    }}