using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class AtivacoesController : ControllerBase    {        [HttpGet]        public IEnumerable<Ativacoes> List()        {            return new AtivacoesService().List();        }        [HttpGet("{idativacoes}", Name = "GetAtivacoes")]        public Ativacoes Get(int idativacoes)        {            return new AtivacoesService().Get(idativacoes);        }        [HttpPut]        public Int32 Put(Ativacoes ativacoes)        {            return new AtivacoesService().Insert(ativacoes);        }        [HttpPatch]        public Int32 Post(Ativacoes ativacoes)        {            return new AtivacoesService().Update(ativacoes);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idativacoes)        {            return new AtivacoesService().Delete(idativacoes);        }    }}