using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class BancosController : ControllerBase    {        [HttpGet]        public IEnumerable<Bancos> List()        {            return new BancosService().List();        }        [HttpGet("{idbancos}", Name = "GetBancos")]        public Bancos Get(int idbancos)        {            return new BancosService().Get(idbancos);        }        [HttpPut]        public Int32 Put(Bancos bancos)        {            return new BancosService().Insert(bancos);        }        [HttpPatch]        public Int32 Post(Bancos bancos)        {            return new BancosService().Update(bancos);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idbancos)        {            return new BancosService().Delete(idbancos);        }    }}