using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class ConfiguracoesController : ControllerBase    {        [HttpGet]        public IEnumerable<Configuracoes> List()        {            return new ConfiguracoesService().List();        }        [HttpGet("{idconfiguracoes}", Name = "GetConfiguracoes")]        public Configuracoes Get(int idconfiguracoes)        {            return new ConfiguracoesService().Get(idconfiguracoes);        }        [HttpPut]        public Int32 Put(Configuracoes configuracoes)        {            return new ConfiguracoesService().Insert(configuracoes);        }        [HttpPatch]        public Int32 Post(Configuracoes configuracoes)        {            return new ConfiguracoesService().Update(configuracoes);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idconfiguracoes)        {            return new ConfiguracoesService().Delete(idconfiguracoes);        }    }}