using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class ContacorrenteController : ControllerBase    {        [HttpGet]        public IEnumerable<Contacorrente> List()        {            return new ContacorrenteService().List();        }        [HttpGet("{idcontacorrente}", Name = "GetContacorrente")]        public Contacorrente Get(int idcontacorrente)        {            return new ContacorrenteService().Get(idcontacorrente);        }        [HttpPut]        public Int32 Put(Contacorrente contacorrente)        {            return new ContacorrenteService().Insert(contacorrente);        }        [HttpPatch]        public Int32 Post(Contacorrente contacorrente)        {            return new ContacorrenteService().Update(contacorrente);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idcontacorrente)        {            return new ContacorrenteService().Delete(idcontacorrente);        }    }}