using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class DadosbancariosassociadosController : ControllerBase    {        [HttpGet]        public IEnumerable<Dadosbancariosassociados> List()        {            return new DadosbancariosassociadosService().List();        }        [HttpGet("{iddadosbancariosassociados}", Name = "GetDadosbancariosassociados")]        public Dadosbancariosassociados Get(int iddadosbancariosassociados)        {            return new DadosbancariosassociadosService().Get(iddadosbancariosassociados);        }        [HttpPut]        public Int32 Put(Dadosbancariosassociados dadosbancariosassociados)        {            return new DadosbancariosassociadosService().Insert(dadosbancariosassociados);        }        [HttpPatch]        public Int32 Post(Dadosbancariosassociados dadosbancariosassociados)        {            return new DadosbancariosassociadosService().Update(dadosbancariosassociados);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 iddadosbancariosassociados)        {            return new DadosbancariosassociadosService().Delete(iddadosbancariosassociados);        }    }}