using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class DetalhepedidoController : ControllerBase    {        [HttpGet]        public IEnumerable<Detalhepedido> List()        {            return new DetalhepedidoService().List();        }        [HttpGet("{iddetalhepedido}", Name = "GetDetalhepedido")]        public Detalhepedido Get(int iddetalhepedido)        {            return new DetalhepedidoService().Get(iddetalhepedido);        }        [HttpPut]        public Int32 Put(Detalhepedido detalhepedido)        {            return new DetalhepedidoService().Insert(detalhepedido);        }        [HttpPatch]        public Int32 Post(Detalhepedido detalhepedido)        {            return new DetalhepedidoService().Update(detalhepedido);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 iddetalhepedido)        {            return new DetalhepedidoService().Delete(iddetalhepedido);        }    }}