using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class EmpresaController : ControllerBase    {        [HttpGet]        public IEnumerable<Empresa> List()        {            return new EmpresaService().List();        }        [HttpGet("{idempresa}", Name = "GetEmpresa")]        public Empresa Get(int idempresa)        {            return new EmpresaService().Get(idempresa);        }        [HttpPut]        public Int32 Put(Empresa empresa)        {            return new EmpresaService().Insert(empresa);        }        [HttpPatch]        public Int32 Post(Empresa empresa)        {            return new EmpresaService().Update(empresa);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idempresa)        {            return new EmpresaService().Delete(idempresa);        }    }}