using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class FabricantesController : ControllerBase    {        [HttpGet]        public IEnumerable<Fabricantes> List()        {            return new FabricantesService().List();        }        [HttpGet("{idfabricantes}", Name = "GetFabricantes")]        public Fabricantes Get(int idfabricantes)        {            return new FabricantesService().Get(idfabricantes);        }        [HttpPut]        public Int32 Put(Fabricantes fabricantes)        {            return new FabricantesService().Insert(fabricantes);        }        [HttpPatch]        public Int32 Post(Fabricantes fabricantes)        {            return new FabricantesService().Update(fabricantes);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idfabricantes)        {            return new FabricantesService().Delete(idfabricantes);        }    }}