using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class ImagensprodutosController : ControllerBase    {        [HttpGet]        public IEnumerable<Imagensprodutos> List()        {            return new ImagensprodutosService().List();        }        [HttpGet("{idimagensprodutos}", Name = "GetImagensprodutos")]        public Imagensprodutos Get(int idimagensprodutos)        {            return new ImagensprodutosService().Get(idimagensprodutos);        }        [HttpPut]        public Int32 Put(Imagensprodutos imagensprodutos)        {            return new ImagensprodutosService().Insert(imagensprodutos);        }        [HttpPatch]        public Int32 Post(Imagensprodutos imagensprodutos)        {            return new ImagensprodutosService().Update(imagensprodutos);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idimagensprodutos)        {            return new ImagensprodutosService().Delete(idimagensprodutos);        }    }}