using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class LancamentoscontacorrenteController : ControllerBase    {        [HttpGet]        public IEnumerable<Lancamentoscontacorrente> List()        {            return new LancamentoscontacorrenteService().List();        }        [HttpGet("{idlancamentoscontacorrente}", Name = "GetLancamentoscontacorrente")]        public Lancamentoscontacorrente Get(int idlancamentoscontacorrente)        {            return new LancamentoscontacorrenteService().Get(idlancamentoscontacorrente);        }        [HttpPut]        public Int32 Put(Lancamentoscontacorrente lancamentoscontacorrente)        {            return new LancamentoscontacorrenteService().Insert(lancamentoscontacorrente);        }        [HttpPatch]        public Int32 Post(Lancamentoscontacorrente lancamentoscontacorrente)        {            return new LancamentoscontacorrenteService().Update(lancamentoscontacorrente);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idlancamentoscontacorrente)        {            return new LancamentoscontacorrenteService().Delete(idlancamentoscontacorrente);        }    }}