using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class MovimentacoesController : ControllerBase    {        [HttpGet]        public IEnumerable<Movimentacoes> List()        {            return new MovimentacoesService().List();        }        [HttpGet("{idmovimentacoes}", Name = "GetMovimentacoes")]        public Movimentacoes Get(int idmovimentacoes)        {            return new MovimentacoesService().Get(idmovimentacoes);        }        [HttpPut]        public Int32 Put(Movimentacoes movimentacoes)        {            return new MovimentacoesService().Insert(movimentacoes);        }        [HttpPatch]        public Int32 Post(Movimentacoes movimentacoes)        {            return new MovimentacoesService().Update(movimentacoes);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idmovimentacoes)        {            return new MovimentacoesService().Delete(idmovimentacoes);        }    }}