using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class NiveisController : ControllerBase    {        [HttpGet]        public IEnumerable<Niveis> List()        {            return new NiveisService().List();        }        [HttpGet("{idniveis}", Name = "GetNiveis")]        public Niveis Get(int idniveis)        {            return new NiveisService().Get(idniveis);        }        [HttpPut]        public Int32 Put(Niveis niveis)        {            return new NiveisService().Insert(niveis);        }        [HttpPatch]        public Int32 Post(Niveis niveis)        {            return new NiveisService().Update(niveis);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idniveis)        {            return new NiveisService().Delete(idniveis);        }    }}