using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class PagamentopedidoController : ControllerBase    {        [HttpGet]        public IEnumerable<Pagamentopedido> List()        {            return new PagamentopedidoService().List();        }        [HttpGet("{idpagamentopedido}", Name = "GetPagamentopedido")]        public Pagamentopedido Get(int idpagamentopedido)        {            return new PagamentopedidoService().Get(idpagamentopedido);        }        [HttpPut]        public Int32 Put(Pagamentopedido pagamentopedido)        {            return new PagamentopedidoService().Insert(pagamentopedido);        }        [HttpPatch]        public Int32 Post(Pagamentopedido pagamentopedido)        {            return new PagamentopedidoService().Update(pagamentopedido);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idpagamentopedido)        {            return new PagamentopedidoService().Delete(idpagamentopedido);        }    }}