using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class PedidosController : ControllerBase    {        [HttpGet]        public IEnumerable<Pedidos> List()        {            return new PedidosService().List();        }        [HttpGet("{idpedidos}", Name = "GetPedidos")]        public Pedidos Get(int idpedidos)        {            return new PedidosService().Get(idpedidos);        }        [HttpPut]        public Int32 Put(Pedidos pedidos)        {            return new PedidosService().Insert(pedidos);        }        [HttpPatch]        public Int32 Post(Pedidos pedidos)        {            return new PedidosService().Update(pedidos);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idpedidos)        {            return new PedidosService().Delete(idpedidos);        }    }}