using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class PedidoscomissionadosController : ControllerBase    {        [HttpGet]        public IEnumerable<Pedidoscomissionados> List()        {            return new PedidoscomissionadosService().List();        }        [HttpGet("{idpedidoscomissionados}", Name = "GetPedidoscomissionados")]        public Pedidoscomissionados Get(int idpedidoscomissionados)        {            return new PedidoscomissionadosService().Get(idpedidoscomissionados);        }        [HttpPut]        public Int32 Put(Pedidoscomissionados pedidoscomissionados)        {            return new PedidoscomissionadosService().Insert(pedidoscomissionados);        }        [HttpPatch]        public Int32 Post(Pedidoscomissionados pedidoscomissionados)        {            return new PedidoscomissionadosService().Update(pedidoscomissionados);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idpedidoscomissionados)        {            return new PedidoscomissionadosService().Delete(idpedidoscomissionados);        }    }}