using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class PontoapoioController : ControllerBase    {        [HttpGet]        public IEnumerable<Pontoapoio> List()        {            return new PontoapoioService().List();        }        [HttpGet("{idpontoapoio}", Name = "GetPontoapoio")]        public Pontoapoio Get(int idpontoapoio)        {            return new PontoapoioService().Get(idpontoapoio);        }        [HttpPut]        public Int32 Put(Pontoapoio pontoapoio)        {            return new PontoapoioService().Insert(pontoapoio);        }        [HttpPatch]        public Int32 Post(Pontoapoio pontoapoio)        {            return new PontoapoioService().Update(pontoapoio);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idpontoapoio)        {            return new PontoapoioService().Delete(idpontoapoio);        }    }}