using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class PrecosController : ControllerBase    {        [HttpGet]        public IEnumerable<Precos> List()        {            return new PrecosService().List();        }        [HttpGet("{idprecos}", Name = "GetPrecos")]        public Precos Get(int idprecos)        {            return new PrecosService().Get(idprecos);        }        [HttpPut]        public Int32 Put(Precos precos)        {            return new PrecosService().Insert(precos);        }        [HttpPatch]        public Int32 Post(Precos precos)        {            return new PrecosService().Update(precos);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idprecos)        {            return new PrecosService().Delete(idprecos);        }    }}