using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class ProdutosController : ControllerBase    {        [HttpGet]        public IEnumerable<Produtos> List()        {            return new ProdutosService().List();        }        [HttpGet("{idprodutos}", Name = "GetProdutos")]        public Produtos Get(int idprodutos)        {            return new ProdutosService().Get(idprodutos);        }        [HttpPut]        public Int32 Put(Produtos produtos)        {            return new ProdutosService().Insert(produtos);        }        [HttpPatch]        public Int32 Post(Produtos produtos)        {            return new ProdutosService().Update(produtos);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idprodutos)        {            return new ProdutosService().Delete(idprodutos);        }    }}