using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class TipoadesaoController : ControllerBase    {        [HttpGet]        public IEnumerable<Tipoadesao> List()        {            return new TipoadesaoService().List();        }        [HttpGet("{idtipoassociado}", Name = "GetTipoadesao")]        public Tipoadesao Get(int idtipoassociado)        {            return new TipoadesaoService().Get(idtipoassociado);        }        [HttpPut]        public Int32 Put(Tipoadesao tipoadesao)        {            return new TipoadesaoService().Insert(tipoadesao);        }        [HttpPatch]        public Int32 Post(Tipoadesao tipoadesao)        {            return new TipoadesaoService().Update(tipoadesao);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idtipoassociado)        {            return new TipoadesaoService().Delete(idtipoassociado);        }    }}