using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class TipomovimentacaoController : ControllerBase    {        [HttpGet]        public IEnumerable<Tipomovimentacao> List()        {            return new TipomovimentacaoService().List();        }        [HttpGet("{idtipomovimentacao}", Name = "GetTipomovimentacao")]        public Tipomovimentacao Get(int idtipomovimentacao)        {            return new TipomovimentacaoService().Get(idtipomovimentacao);        }        [HttpPut]        public Int32 Put(Tipomovimentacao tipomovimentacao)        {            return new TipomovimentacaoService().Insert(tipomovimentacao);        }        [HttpPatch]        public Int32 Post(Tipomovimentacao tipomovimentacao)        {            return new TipomovimentacaoService().Update(tipomovimentacao);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idtipomovimentacao)        {            return new TipomovimentacaoService().Delete(idtipomovimentacao);        }    }}