using System;using System.Collections.Generic;using System.Linq;using System.Net.Http;using System.Threading.Tasks;using drhipocratesCore.Model;using drhipocratesCore.Service;using Microsoft.AspNetCore.Mvc;namespace drhipocratesCore.Controllers{    [ApiController]    [Route("[controller]")]    public class TipousuarioController : ControllerBase    {        [HttpGet]        public IEnumerable<Tipousuario> List()        {            return new TipousuarioService().List();        }        [HttpGet("{idtipousuario}", Name = "GetTipousuario")]        public Tipousuario Get(int idtipousuario)        {            return new TipousuarioService().Get(idtipousuario);        }        [HttpPut]        public Int32 Put(Tipousuario tipousuario)        {            return new TipousuarioService().Insert(tipousuario);        }        [HttpPatch]        public Int32 Post(Tipousuario tipousuario)        {            return new TipousuarioService().Update(tipousuario);        }        [HttpDelete]        public Int32 Delete([FromQuery]Int32 idtipousuario)        {            return new TipousuarioService().Delete(idtipousuario);        }    }}