using System;using System.Collections.Generic;using System.Data;using System.Linq;using System.Threading.Tasks;using drhipocratesCore.Config;using drhipocratesCore.Model;using MySql.Data;using MySql.Data.MySqlClient;namespace drhipocratesCore.Repository{   public class AtivacoesRepository   {      String connect = Connect.getConnect;              public List<Ativacoes> List()         {             MySqlConnection conn = new MySqlConnection(connect);             System.Text.StringBuilder query = new System.Text.StringBuilder();             query.Append("select idativacoes, idassociado, ultimaativacao from ativacoes");                  MySqlCommand comm = new MySqlCommand(query.ToString(), conn);             MySqlDataReader dr = null;             List<Ativacoes> lista = new List<Ativacoes>();                  try             {                 conn.Open();                 dr = comm.ExecuteReader(CommandBehavior.CloseConnection);                      while (dr.Read())                     {                     Ativacoes unitario = new Ativacoes();                          unitario.idativacoes = dr.IsDBNull(0) ? 0 : dr.GetInt32(0);                     unitario.idassociado = dr.IsDBNull(1) ? 0 : dr.GetInt32(1);                     unitario.ultimaativacao = dr.IsDBNull(2) ? Convert.ToDateTime( "01/01/0001 00:00:00" ) : dr.GetDateTime(2);                          lista.Add(unitario);                     }             }             catch (Exception ex)             {                 string ERRO = ex.Message;                 return new List<Ativacoes>();             }             finally             { conn.Close(); }                  return lista;         }              public Ativacoes Get(int primary_key)         {             MySqlConnection conn = new MySqlConnection(connect);             System.Text.StringBuilder query = new System.Text.StringBuilder();             query.Append("select idativacoes, idassociado, ultimaativacao from ativacoes where idativacoes = @idativacoes");                  MySqlCommand comm = new MySqlCommand(query.ToString(), conn);             comm.Parameters.Add("@idativacoes", MySqlDbType.Int32);             comm.Parameters[0].Value = primary_key;             MySqlDataReader dr = null;             Ativacoes lista = new Ativacoes();             try             {                 conn.Open();                 dr = comm.ExecuteReader(CommandBehavior.CloseConnection);                      while (dr.Read())                     {                     Ativacoes unitario = new Ativacoes();                          unitario.idativacoes = dr.IsDBNull(0) ? 0 : dr.GetInt32(0);                     unitario.idassociado = dr.IsDBNull(1) ? 0 : dr.GetInt32(1);                     unitario.ultimaativacao = dr.IsDBNull(2) ? Convert.ToDateTime( "01/01/0001 00:00:00" ) : dr.GetDateTime(2);                          lista = unitario;                     }             }             catch (Exception ex)             {                 string ERRO = ex.Message;                 return new Ativacoes();             }             finally             { conn.Close(); }                  return lista;         }    public int Post(Ativacoes ativacoes)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("insert into ativacoes (idassociado, ultimaativacao) values (@idassociado, @ultimaativacao)");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idassociado", MySqlDbType.Int32 );        comm.Parameters.Add("@ultimaativacao", MySqlDbType.DateTime );        comm.Parameters[0].Value = ativacoes.idassociado;        comm.Parameters[1].Value = ativacoes.ultimaativacao;        Int32 id = 0;        try        {            conn.Open();            comm.ExecuteNonQuery();            query.Clear();            query.Append("Select @@Identity");            comm.CommandText = query.ToString();            var resultado = comm.ExecuteScalar();            id = Convert.ToInt32(resultado);        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }        return id;    }    public int Put(Ativacoes ativacoes)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("update ativacoes set idativacoes = @idativacoes, idassociado = @idassociado, ultimaativacao = @ultimaativacao where idativacoes = @idativacoes");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idativacoes", MySqlDbType.Int32 );        comm.Parameters.Add("@idassociado", MySqlDbType.Int32 );        comm.Parameters.Add("@ultimaativacao", MySqlDbType.DateTime );        comm.Parameters[0].Value = ativacoes.idativacoes;        comm.Parameters[1].Value = ativacoes.idassociado;        comm.Parameters[2].Value = ativacoes.ultimaativacao;        try        {            conn.Open();            return comm.ExecuteNonQuery();        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }    }    public int Delete(int idativacoes)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("delete from ativacoes where idativacoes = @idativacoes");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idativacoes", MySqlDbType.Int32 );        comm.Parameters[0].Value = idativacoes;        try        {            conn.Open();            return comm.ExecuteNonQuery();        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }    }   }}