using System;using System.Collections.Generic;using System.Data;using System.Linq;using System.Threading.Tasks;using drhipocratesCore.Config;using drhipocratesCore.Model;using MySql.Data;using MySql.Data.MySqlClient;namespace drhipocratesCore.Repository{   public class DetalhepedidoRepository   {      String connect = Connect.getConnect;              public List<Detalhepedido> List()         {             MySqlConnection conn = new MySqlConnection(connect);             System.Text.StringBuilder query = new System.Text.StringBuilder();             query.Append("select iddetalhepedido, idpedido, idproduto, quantidadeproduto, valorunitarioproduto from detalhepedido");                  MySqlCommand comm = new MySqlCommand(query.ToString(), conn);             MySqlDataReader dr = null;             List<Detalhepedido> lista = new List<Detalhepedido>();                  try             {                 conn.Open();                 dr = comm.ExecuteReader(CommandBehavior.CloseConnection);                      while (dr.Read())                     {                     Detalhepedido unitario = new Detalhepedido();                          unitario.iddetalhepedido = dr.IsDBNull(0) ? 0 : dr.GetInt32(0);                     unitario.idpedido = dr.IsDBNull(1) ? 0 : dr.GetInt32(1);                     unitario.idproduto = dr.IsDBNull(2) ? 0 : dr.GetInt32(2);                     unitario.quantidadeproduto = dr.IsDBNull(3) ? 0 : dr.GetInt32(3);                     unitario.valorunitarioproduto = dr.IsDBNull(4) ? 0 : dr.GetFloat(4);                          lista.Add(unitario);                     }             }             catch (Exception ex)             {                 string ERRO = ex.Message;                 return new List<Detalhepedido>();             }             finally             { conn.Close(); }                  return lista;         }              public Detalhepedido Get(int primary_key)         {             MySqlConnection conn = new MySqlConnection(connect);             System.Text.StringBuilder query = new System.Text.StringBuilder();             query.Append("select iddetalhepedido, idpedido, idproduto, quantidadeproduto, valorunitarioproduto from detalhepedido where iddetalhepedido = @iddetalhepedido");                  MySqlCommand comm = new MySqlCommand(query.ToString(), conn);             comm.Parameters.Add("@iddetalhepedido", MySqlDbType.Int32);             comm.Parameters[0].Value = primary_key;             MySqlDataReader dr = null;             Detalhepedido lista = new Detalhepedido();             try             {                 conn.Open();                 dr = comm.ExecuteReader(CommandBehavior.CloseConnection);                      while (dr.Read())                     {                     Detalhepedido unitario = new Detalhepedido();                          unitario.iddetalhepedido = dr.IsDBNull(0) ? 0 : dr.GetInt32(0);                     unitario.idpedido = dr.IsDBNull(1) ? 0 : dr.GetInt32(1);                     unitario.idproduto = dr.IsDBNull(2) ? 0 : dr.GetInt32(2);                     unitario.quantidadeproduto = dr.IsDBNull(3) ? 0 : dr.GetInt32(3);                     unitario.valorunitarioproduto = dr.IsDBNull(4) ? 0 : dr.GetFloat(4);                          lista = unitario;                     }             }             catch (Exception ex)             {                 string ERRO = ex.Message;                 return new Detalhepedido();             }             finally             { conn.Close(); }                  return lista;         }    public int Post(Detalhepedido detalhepedido)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("insert into detalhepedido (idpedido, idproduto, quantidadeproduto, valorunitarioproduto) values (@idpedido, @idproduto, @quantidadeproduto, @valorunitarioproduto)");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idpedido", MySqlDbType.Int32 );        comm.Parameters.Add("@idproduto", MySqlDbType.Int32 );        comm.Parameters.Add("@quantidadeproduto", MySqlDbType.Int32 );        comm.Parameters.Add("@valorunitarioproduto", MySqlDbType.Float );        comm.Parameters[0].Value = detalhepedido.idpedido;        comm.Parameters[1].Value = detalhepedido.idproduto;        comm.Parameters[2].Value = detalhepedido.quantidadeproduto;        comm.Parameters[3].Value = detalhepedido.valorunitarioproduto;        Int32 id = 0;        try        {            conn.Open();            comm.ExecuteNonQuery();            query.Clear();            query.Append("Select @@Identity");            comm.CommandText = query.ToString();            var resultado = comm.ExecuteScalar();            id = Convert.ToInt32(resultado);        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }        return id;    }    public int Put(Detalhepedido detalhepedido)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("update detalhepedido set iddetalhepedido = @iddetalhepedido, idpedido = @idpedido, idproduto = @idproduto, quantidadeproduto = @quantidadeproduto, valorunitarioproduto = @valorunitarioproduto where iddetalhepedido = @iddetalhepedido");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@iddetalhepedido", MySqlDbType.Int32 );        comm.Parameters.Add("@idpedido", MySqlDbType.Int32 );        comm.Parameters.Add("@idproduto", MySqlDbType.Int32 );        comm.Parameters.Add("@quantidadeproduto", MySqlDbType.Int32 );        comm.Parameters.Add("@valorunitarioproduto", MySqlDbType.Float );        comm.Parameters[0].Value = detalhepedido.iddetalhepedido;        comm.Parameters[1].Value = detalhepedido.idpedido;        comm.Parameters[2].Value = detalhepedido.idproduto;        comm.Parameters[3].Value = detalhepedido.quantidadeproduto;        comm.Parameters[4].Value = detalhepedido.valorunitarioproduto;        try        {            conn.Open();            return comm.ExecuteNonQuery();        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }    }    public int Delete(int iddetalhepedido)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("delete from detalhepedido where iddetalhepedido = @iddetalhepedido");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@iddetalhepedido", MySqlDbType.Int32 );        comm.Parameters[0].Value = iddetalhepedido;        try        {            conn.Open();            return comm.ExecuteNonQuery();        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }    }   }}