using System;using System.Collections.Generic;using System.Data;using System.Linq;using System.Threading.Tasks;using drhipocratesCore.Config;using drhipocratesCore.Model;using MySql.Data;using MySql.Data.MySqlClient;namespace drhipocratesCore.Repository{   public class MovimentacoesRepository   {      String connect = Connect.getConnect;              public List<Movimentacoes> List()         {             MySqlConnection conn = new MySqlConnection(connect);             System.Text.StringBuilder query = new System.Text.StringBuilder();             query.Append("select idmovimentacoes, idtipomovimentacao, idassociado, valormovimentacao, datamovimentacao from movimentacoes");                  MySqlCommand comm = new MySqlCommand(query.ToString(), conn);             MySqlDataReader dr = null;             List<Movimentacoes> lista = new List<Movimentacoes>();                  try             {                 conn.Open();                 dr = comm.ExecuteReader(CommandBehavior.CloseConnection);                      while (dr.Read())                     {                     Movimentacoes unitario = new Movimentacoes();                          unitario.idmovimentacoes = dr.IsDBNull(0) ? 0 : dr.GetInt32(0);                     unitario.idtipomovimentacao = dr.IsDBNull(1) ? 0 : dr.GetInt32(1);                     unitario.idassociado = dr.IsDBNull(2) ? 0 : dr.GetInt32(2);                     unitario.valormovimentacao = dr.IsDBNull(3) ? 0 : dr.GetFloat(3);                     unitario.datamovimentacao = dr.IsDBNull(4) ? Convert.ToDateTime( "01/01/0001 00:00:00" ) : dr.GetDateTime(4);                          lista.Add(unitario);                     }             }             catch (Exception ex)             {                 string ERRO = ex.Message;                 return new List<Movimentacoes>();             }             finally             { conn.Close(); }                  return lista;         }              public Movimentacoes Get(int primary_key)         {             MySqlConnection conn = new MySqlConnection(connect);             System.Text.StringBuilder query = new System.Text.StringBuilder();             query.Append("select idmovimentacoes, idtipomovimentacao, idassociado, valormovimentacao, datamovimentacao from movimentacoes where idmovimentacoes = @idmovimentacoes");                  MySqlCommand comm = new MySqlCommand(query.ToString(), conn);             comm.Parameters.Add("@idmovimentacoes", MySqlDbType.Int32);             comm.Parameters[0].Value = primary_key;             MySqlDataReader dr = null;             Movimentacoes lista = new Movimentacoes();             try             {                 conn.Open();                 dr = comm.ExecuteReader(CommandBehavior.CloseConnection);                      while (dr.Read())                     {                     Movimentacoes unitario = new Movimentacoes();                          unitario.idmovimentacoes = dr.IsDBNull(0) ? 0 : dr.GetInt32(0);                     unitario.idtipomovimentacao = dr.IsDBNull(1) ? 0 : dr.GetInt32(1);                     unitario.idassociado = dr.IsDBNull(2) ? 0 : dr.GetInt32(2);                     unitario.valormovimentacao = dr.IsDBNull(3) ? 0 : dr.GetFloat(3);                     unitario.datamovimentacao = dr.IsDBNull(4) ? Convert.ToDateTime( "01/01/0001 00:00:00" ) : dr.GetDateTime(4);                          lista = unitario;                     }             }             catch (Exception ex)             {                 string ERRO = ex.Message;                 return new Movimentacoes();             }             finally             { conn.Close(); }                  return lista;         }    public int Post(Movimentacoes movimentacoes)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("insert into movimentacoes (idtipomovimentacao, idassociado, valormovimentacao, datamovimentacao) values (@idtipomovimentacao, @idassociado, @valormovimentacao, @datamovimentacao)");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idtipomovimentacao", MySqlDbType.Int32 );        comm.Parameters.Add("@idassociado", MySqlDbType.Int32 );        comm.Parameters.Add("@valormovimentacao", MySqlDbType.Float );        comm.Parameters.Add("@datamovimentacao", MySqlDbType.DateTime );        comm.Parameters[0].Value = movimentacoes.idtipomovimentacao;        comm.Parameters[1].Value = movimentacoes.idassociado;        comm.Parameters[2].Value = movimentacoes.valormovimentacao;        comm.Parameters[3].Value = movimentacoes.datamovimentacao;        Int32 id = 0;        try        {            conn.Open();            comm.ExecuteNonQuery();            query.Clear();            query.Append("Select @@Identity");            comm.CommandText = query.ToString();            var resultado = comm.ExecuteScalar();            id = Convert.ToInt32(resultado);        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }        return id;    }    public int Put(Movimentacoes movimentacoes)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("update movimentacoes set idmovimentacoes = @idmovimentacoes, idtipomovimentacao = @idtipomovimentacao, idassociado = @idassociado, valormovimentacao = @valormovimentacao, datamovimentacao = @datamovimentacao where idmovimentacoes = @idmovimentacoes");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idmovimentacoes", MySqlDbType.Int32 );        comm.Parameters.Add("@idtipomovimentacao", MySqlDbType.Int32 );        comm.Parameters.Add("@idassociado", MySqlDbType.Int32 );        comm.Parameters.Add("@valormovimentacao", MySqlDbType.Float );        comm.Parameters.Add("@datamovimentacao", MySqlDbType.DateTime );        comm.Parameters[0].Value = movimentacoes.idmovimentacoes;        comm.Parameters[1].Value = movimentacoes.idtipomovimentacao;        comm.Parameters[2].Value = movimentacoes.idassociado;        comm.Parameters[3].Value = movimentacoes.valormovimentacao;        comm.Parameters[4].Value = movimentacoes.datamovimentacao;        try        {            conn.Open();            return comm.ExecuteNonQuery();        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }    }    public int Delete(int idmovimentacoes)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("delete from movimentacoes where idmovimentacoes = @idmovimentacoes");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idmovimentacoes", MySqlDbType.Int32 );        comm.Parameters[0].Value = idmovimentacoes;        try        {            conn.Open();            return comm.ExecuteNonQuery();        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }    }   }}