using System;using System.Collections.Generic;using System.Data;using System.Linq;using System.Threading.Tasks;using drhipocratesCore.Config;using drhipocratesCore.Model;using MySql.Data;using MySql.Data.MySqlClient;namespace drhipocratesCore.Repository{   public class PrecosRepository   {      String connect = Connect.getConnect;              public List<Precos> List()         {             MySqlConnection conn = new MySqlConnection(connect);             System.Text.StringBuilder query = new System.Text.StringBuilder();             query.Append("select idprecos, idproduto, precoativacao, precoecommerce, tipocampanha from precos");                  MySqlCommand comm = new MySqlCommand(query.ToString(), conn);             MySqlDataReader dr = null;             List<Precos> lista = new List<Precos>();                  try             {                 conn.Open();                 dr = comm.ExecuteReader(CommandBehavior.CloseConnection);                      while (dr.Read())                     {                     Precos unitario = new Precos();                          unitario.idprecos = dr.IsDBNull(0) ? 0 : dr.GetInt32(0);                     unitario.idproduto = dr.IsDBNull(1) ? 0 : dr.GetInt32(1);                     unitario.precoativacao = dr.IsDBNull(2) ? 0 : dr.GetFloat(2);                     unitario.precoecommerce = dr.IsDBNull(3) ? 0 : dr.GetFloat(3);                     unitario.tipocampanha = dr.IsDBNull(4) ? 0 : dr.GetInt32(4);                          lista.Add(unitario);                     }             }             catch (Exception ex)             {                 string ERRO = ex.Message;                 return new List<Precos>();             }             finally             { conn.Close(); }                  return lista;         }              public Precos Get(int primary_key)         {             MySqlConnection conn = new MySqlConnection(connect);             System.Text.StringBuilder query = new System.Text.StringBuilder();             query.Append("select idprecos, idproduto, precoativacao, precoecommerce, tipocampanha from precos where idprecos = @idprecos");                  MySqlCommand comm = new MySqlCommand(query.ToString(), conn);             comm.Parameters.Add("@idprecos", MySqlDbType.Int32);             comm.Parameters[0].Value = primary_key;             MySqlDataReader dr = null;             Precos lista = new Precos();             try             {                 conn.Open();                 dr = comm.ExecuteReader(CommandBehavior.CloseConnection);                      while (dr.Read())                     {                     Precos unitario = new Precos();                          unitario.idprecos = dr.IsDBNull(0) ? 0 : dr.GetInt32(0);                     unitario.idproduto = dr.IsDBNull(1) ? 0 : dr.GetInt32(1);                     unitario.precoativacao = dr.IsDBNull(2) ? 0 : dr.GetFloat(2);                     unitario.precoecommerce = dr.IsDBNull(3) ? 0 : dr.GetFloat(3);                     unitario.tipocampanha = dr.IsDBNull(4) ? 0 : dr.GetInt32(4);                          lista = unitario;                     }             }             catch (Exception ex)             {                 string ERRO = ex.Message;                 return new Precos();             }             finally             { conn.Close(); }                  return lista;         }    public int Post(Precos precos)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("insert into precos (idproduto, precoativacao, precoecommerce, tipocampanha) values (@idproduto, @precoativacao, @precoecommerce, @tipocampanha)");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idproduto", MySqlDbType.Int32 );        comm.Parameters.Add("@precoativacao", MySqlDbType.Float );        comm.Parameters.Add("@precoecommerce", MySqlDbType.Float );        comm.Parameters.Add("@tipocampanha", MySqlDbType.Int32 );        comm.Parameters[0].Value = precos.idproduto;        comm.Parameters[1].Value = precos.precoativacao;        comm.Parameters[2].Value = precos.precoecommerce;        comm.Parameters[3].Value = precos.tipocampanha;        Int32 id = 0;        try        {            conn.Open();            comm.ExecuteNonQuery();            query.Clear();            query.Append("Select @@Identity");            comm.CommandText = query.ToString();            var resultado = comm.ExecuteScalar();            id = Convert.ToInt32(resultado);        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }        return id;    }    public int Put(Precos precos)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("update precos set idprecos = @idprecos, idproduto = @idproduto, precoativacao = @precoativacao, precoecommerce = @precoecommerce, tipocampanha = @tipocampanha where idprecos = @idprecos");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idprecos", MySqlDbType.Int32 );        comm.Parameters.Add("@idproduto", MySqlDbType.Int32 );        comm.Parameters.Add("@precoativacao", MySqlDbType.Float );        comm.Parameters.Add("@precoecommerce", MySqlDbType.Float );        comm.Parameters.Add("@tipocampanha", MySqlDbType.Int32 );        comm.Parameters[0].Value = precos.idprecos;        comm.Parameters[1].Value = precos.idproduto;        comm.Parameters[2].Value = precos.precoativacao;        comm.Parameters[3].Value = precos.precoecommerce;        comm.Parameters[4].Value = precos.tipocampanha;        try        {            conn.Open();            return comm.ExecuteNonQuery();        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }    }    public int Delete(int idprecos)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("delete from precos where idprecos = @idprecos");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idprecos", MySqlDbType.Int32 );        comm.Parameters[0].Value = idprecos;        try        {            conn.Open();            return comm.ExecuteNonQuery();        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }    }   }}