using System;using System.Collections.Generic;using System.Data;using System.Linq;using System.Threading.Tasks;using drhipocratesCore.Config;using drhipocratesCore.Model;using MySql.Data;using MySql.Data.MySqlClient;namespace drhipocratesCore.Repository{   public class TipoadesaoRepository   {      String connect = Connect.getConnect;              public List<Tipoadesao> List()         {             MySqlConnection conn = new MySqlConnection(connect);             System.Text.StringBuilder query = new System.Text.StringBuilder();             query.Append("select idtipoassociado, descricaotipoassociado, precosugeridoativacao, tipobonus, precosugeridoecommerce from tipoadesao");                  MySqlCommand comm = new MySqlCommand(query.ToString(), conn);             MySqlDataReader dr = null;             List<Tipoadesao> lista = new List<Tipoadesao>();                  try             {                 conn.Open();                 dr = comm.ExecuteReader(CommandBehavior.CloseConnection);                      while (dr.Read())                     {                     Tipoadesao unitario = new Tipoadesao();                          unitario.idtipoassociado = dr.IsDBNull(0) ? 0 : dr.GetInt32(0);                     unitario.descricaotipoassociado = dr.IsDBNull(1) ? "" : dr.GetString(1);                     unitario.precosugeridoativacao = dr.IsDBNull(2) ? 0 : dr.GetFloat(2);                     unitario.tipobonus = dr.IsDBNull(3) ? 0 : dr.GetInt32(3);                     unitario.precosugeridoecommerce = dr.IsDBNull(4) ? 0 : dr.GetFloat(4);                          lista.Add(unitario);                     }             }             catch (Exception ex)             {                 string ERRO = ex.Message;                 return new List<Tipoadesao>();             }             finally             { conn.Close(); }                  return lista;         }              public Tipoadesao Get(int primary_key)         {             MySqlConnection conn = new MySqlConnection(connect);             System.Text.StringBuilder query = new System.Text.StringBuilder();             query.Append("select idtipoassociado, descricaotipoassociado, precosugeridoativacao, tipobonus, precosugeridoecommerce from tipoadesao where idtipoassociado = @idtipoassociado");                  MySqlCommand comm = new MySqlCommand(query.ToString(), conn);             comm.Parameters.Add("@idtipoassociado", MySqlDbType.Int32);             comm.Parameters[0].Value = primary_key;             MySqlDataReader dr = null;             Tipoadesao lista = new Tipoadesao();             try             {                 conn.Open();                 dr = comm.ExecuteReader(CommandBehavior.CloseConnection);                      while (dr.Read())                     {                     Tipoadesao unitario = new Tipoadesao();                          unitario.idtipoassociado = dr.IsDBNull(0) ? 0 : dr.GetInt32(0);                     unitario.descricaotipoassociado = dr.IsDBNull(1) ? "" : dr.GetString(1);                     unitario.precosugeridoativacao = dr.IsDBNull(2) ? 0 : dr.GetFloat(2);                     unitario.tipobonus = dr.IsDBNull(3) ? 0 : dr.GetInt32(3);                     unitario.precosugeridoecommerce = dr.IsDBNull(4) ? 0 : dr.GetFloat(4);                          lista = unitario;                     }             }             catch (Exception ex)             {                 string ERRO = ex.Message;                 return new Tipoadesao();             }             finally             { conn.Close(); }                  return lista;         }    public int Post(Tipoadesao tipoadesao)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("insert into tipoadesao (descricaotipoassociado, precosugeridoativacao, tipobonus, precosugeridoecommerce) values (@descricaotipoassociado, @precosugeridoativacao, @tipobonus, @precosugeridoecommerce)");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@descricaotipoassociado", MySqlDbType.VarChar );        comm.Parameters.Add("@precosugeridoativacao", MySqlDbType.Float );        comm.Parameters.Add("@tipobonus", MySqlDbType.Int32 );        comm.Parameters.Add("@precosugeridoecommerce", MySqlDbType.Float );        comm.Parameters[0].Value = tipoadesao.descricaotipoassociado;        comm.Parameters[1].Value = tipoadesao.precosugeridoativacao;        comm.Parameters[2].Value = tipoadesao.tipobonus;        comm.Parameters[3].Value = tipoadesao.precosugeridoecommerce;        Int32 id = 0;        try        {            conn.Open();            comm.ExecuteNonQuery();            query.Clear();            query.Append("Select @@Identity");            comm.CommandText = query.ToString();            var resultado = comm.ExecuteScalar();            id = Convert.ToInt32(resultado);        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }        return id;    }    public int Put(Tipoadesao tipoadesao)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("update tipoadesao set idtipoassociado = @idtipoassociado, descricaotipoassociado = @descricaotipoassociado, precosugeridoativacao = @precosugeridoativacao, tipobonus = @tipobonus, precosugeridoecommerce = @precosugeridoecommerce where idtipoassociado = @idtipoassociado");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idtipoassociado", MySqlDbType.Int32 );        comm.Parameters.Add("@descricaotipoassociado", MySqlDbType.VarChar );        comm.Parameters.Add("@precosugeridoativacao", MySqlDbType.Float );        comm.Parameters.Add("@tipobonus", MySqlDbType.Int32 );        comm.Parameters.Add("@precosugeridoecommerce", MySqlDbType.Float );        comm.Parameters[0].Value = tipoadesao.idtipoassociado;        comm.Parameters[1].Value = tipoadesao.descricaotipoassociado;        comm.Parameters[2].Value = tipoadesao.precosugeridoativacao;        comm.Parameters[3].Value = tipoadesao.tipobonus;        comm.Parameters[4].Value = tipoadesao.precosugeridoecommerce;        try        {            conn.Open();            return comm.ExecuteNonQuery();        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }    }    public int Delete(int idtipoassociado)    {        MySqlConnection conn = new MySqlConnection(connect);        System.Text.StringBuilder query = new System.Text.StringBuilder();        query.Append("delete from tipoadesao where idtipoassociado = @idtipoassociado");        MySqlCommand comm = new MySqlCommand(query.ToString(), conn);        comm.Parameters.Add("@idtipoassociado", MySqlDbType.Int32 );        comm.Parameters[0].Value = idtipoassociado;        try        {            conn.Open();            return comm.ExecuteNonQuery();        }        catch (Exception ex)        {            string ERRO = ex.Message;            return 0;        }        finally        { conn.Close(); }    }   }}