using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class AssociadosService    {        public List<Associados> List()        {            return new AssociadosBusiness().List();        }        public Associados Get(int idassociados)        {            return new AssociadosBusiness().Get(idassociados);        }        public Int32 Update(Associados associados)        {            return new AssociadosBusiness().Update(associados);        }        public Int32 Insert(Associados associados)        {            return new AssociadosBusiness().Insert(associados);        }        public Int32 Delete(Int32 idassociados)        {            return new AssociadosBusiness().Delete(idassociados);        }    }}