using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class AtivacoesService    {        public List<Ativacoes> List()        {            return new AtivacoesBusiness().List();        }        public Ativacoes Get(int idativacoes)        {            return new AtivacoesBusiness().Get(idativacoes);        }        public Int32 Update(Ativacoes ativacoes)        {            return new AtivacoesBusiness().Update(ativacoes);        }        public Int32 Insert(Ativacoes ativacoes)        {            return new AtivacoesBusiness().Insert(ativacoes);        }        public Int32 Delete(Int32 idativacoes)        {            return new AtivacoesBusiness().Delete(idativacoes);        }    }}