using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class BancosService    {        public List<Bancos> List()        {            return new BancosBusiness().List();        }        public Bancos Get(int idbancos)        {            return new BancosBusiness().Get(idbancos);        }        public Int32 Update(Bancos bancos)        {            return new BancosBusiness().Update(bancos);        }        public Int32 Insert(Bancos bancos)        {            return new BancosBusiness().Insert(bancos);        }        public Int32 Delete(Int32 idbancos)        {            return new BancosBusiness().Delete(idbancos);        }    }}