using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class ConfiguracoesService    {        public List<Configuracoes> List()        {            return new ConfiguracoesBusiness().List();        }        public Configuracoes Get(int idconfiguracoes)        {            return new ConfiguracoesBusiness().Get(idconfiguracoes);        }        public Int32 Update(Configuracoes configuracoes)        {            return new ConfiguracoesBusiness().Update(configuracoes);        }        public Int32 Insert(Configuracoes configuracoes)        {            return new ConfiguracoesBusiness().Insert(configuracoes);        }        public Int32 Delete(Int32 idconfiguracoes)        {            return new ConfiguracoesBusiness().Delete(idconfiguracoes);        }    }}