using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class ContacorrenteService    {        public List<Contacorrente> List()        {            return new ContacorrenteBusiness().List();        }        public Contacorrente Get(int idcontacorrente)        {            return new ContacorrenteBusiness().Get(idcontacorrente);        }        public Int32 Update(Contacorrente contacorrente)        {            return new ContacorrenteBusiness().Update(contacorrente);        }        public Int32 Insert(Contacorrente contacorrente)        {            return new ContacorrenteBusiness().Insert(contacorrente);        }        public Int32 Delete(Int32 idcontacorrente)        {            return new ContacorrenteBusiness().Delete(idcontacorrente);        }    }}