using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class DetalhepedidoService    {        public List<Detalhepedido> List()        {            return new DetalhepedidoBusiness().List();        }        public Detalhepedido Get(int iddetalhepedido)        {            return new DetalhepedidoBusiness().Get(iddetalhepedido);        }        public Int32 Update(Detalhepedido detalhepedido)        {            return new DetalhepedidoBusiness().Update(detalhepedido);        }        public Int32 Insert(Detalhepedido detalhepedido)        {            return new DetalhepedidoBusiness().Insert(detalhepedido);        }        public Int32 Delete(Int32 iddetalhepedido)        {            return new DetalhepedidoBusiness().Delete(iddetalhepedido);        }    }}