using drhipocratesCore.Model;using drhipocratesCore.Business;using System;using System.Collections.Generic;using System.Linq;using System.Threading.Tasks;namespace drhipocratesCore.Service{    public class EmpresaService    {        public List<Empresa> List()        {            return new EmpresaBusiness().List();        }        public Empresa Get(int idempresa)        {            return new EmpresaBusiness().Get(idempresa);        }        public Int32 Update(Empresa empresa)        {            return new EmpresaBusiness().Update(empresa);        }        public Int32 Insert(Empresa empresa)        {            return new EmpresaBusiness().Insert(empresa);        }        public Int32 Delete(Int32 idempresa)        {            return new EmpresaBusiness().Delete(idempresa);        }    }}